mapper.select:
select

  distinct

'true' as QUERYID,

from sys_menu

  


  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

SELECT a.id,a.name,a.description,a.url,a.type,DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s') as createTime,b.username as createName,a.pid,a.level
from sys_menu a
LEFT JOIN sys_user b on a.create_id=b.id
where a.del_flag=0

	and a.name=#{menuName}

LIMIT ${(pageNo-1)*pageSize},#{pageSize}
