mapper.select:
SELECT opt_time AS total_date, charging_item_id, charging_item_name, COUNT(id) AS call_count, SUM(oper_duration_m) AS duration, SUM(amount) AS total_amout, SUM(charging_amount) as total_charging_amout FROM ( SELECT t.charging_item_id, t.charging_item_name, r.id, r.oper_duration_m, r.amount , r.charging_amount, DATE_FORMAT(r.create_time, '%Y-%m') AS opt_time FROM billing_acct_charging_record r, billing_acct_charging_term t, billing_user_acct c WHERE r.account_id = c.account_id AND r.user_charging_id = t.user_charging_id AND r.oper_duration > 0
AND c.org_code = #{orgCode}
AND ( c.org_code = #{orgCode} OR c.org_code LIKE CONCAT(#{orgCode},'%') )
AND r.oper_user_id = #{operUserId}
AND r.create_time BETWEEN #{beginMonth} AND #{endMonth}
) rt WHERE 1=1 GROUP BY opt_time , charging_item_id, charging_item_name ORDER BY opt_time DESC
limit #{page.indexStart,jdbcType=BIGINT}, #{page.pageSize,jdbcType=INTEGER}

SELECT COUNT(*) FROM ( SELECT opt_time AS total_date, charging_item_id FROM ( SELECT t.charging_item_id, t.charging_item_name, r.id, r.oper_duration_m, r.amount , r.charging_amount, DATE_FORMAT(r.create_time, '%Y-%m') AS opt_time FROM billing_acct_charging_record r, billing_acct_charging_term t, billing_user_acct c WHERE r.account_id = c.account_id AND r.user_charging_id = t.user_charging_id AND r.oper_duration > 0
AND c.org_code = #{orgCode}
AND ( c.org_code = #{orgCode} OR c.org_code LIKE CONCAT(#{orgCode},'%') )
AND r.oper_user_id = #{operUserId}
AND r.create_time BETWEEN #{beginMonth} AND #{endMonth}
) rt WHERE 1 = 1 GROUP BY opt_time , charging_item_id ) a

SELECT opt_time AS total_date, charging_item_id, charging_item_name, COUNT(id) AS call_count, SUM(oper_duration_m) AS duration, SUM(amount) AS total_amout, SUM(charging_amount) as total_charging_amout FROM ( SELECT t.charging_item_id, t.charging_item_name, r.id, r.oper_duration_m, r.amount , r.charging_amount, DATE_FORMAT(r.create_time, '%Y-%m-%d') AS opt_time FROM billing_acct_charging_record r, billing_acct_charging_term t, billing_user_acct c WHERE r.account_id = c.account_id AND r.user_charging_id = t.user_charging_id AND r.oper_duration > 0
AND c.org_code = #{orgCode}
AND ( c.org_code = #{orgCode} OR c.org_code LIKE CONCAT(#{orgCode},'%') )
AND r.oper_user_id = #{operUserId}
AND r.create_time BETWEEN #{beginDate} AND #{endDate}
) rt WHERE 1 = 1 GROUP BY opt_time , charging_item_id, charging_item_name ORDER BY opt_time DESC
limit #{page.indexStart,jdbcType=BIGINT}, #{page.pageSize,jdbcType=INTEGER}

