SELECT
  recharge_amount,
  consume_amount ,
  total_date     AS total_time
FROM billing_total_charging
WHERE del_flag = 0
  AND total_mode = ${@com.guiji.billing.enums.ChargingTotalTypeEnum@DAY.getType()}

    AND total_date BETWEEN #{beginDate} AND #{endDate}

  ORDER BY create_time DESC


      limit #{page.indexStart,jdbcType=BIGINT}, #{page.pageSize,jdbcType=INTEGER}

