mapper.select:
select

  distinct

'true' as QUERYID,

from intention_effect




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into intention_effect (intention_id, process_id, template_id, 
  template_name, version, intention_type, 
  dialogue_times, key_words, crt_time, 
  crt_user, lst_update_time, lst_update_user
  )
values (#{intentionId,jdbcType=VARCHAR}, #{processId,jdbcType=VARCHAR}, #{templateId,jdbcType=VARCHAR}, 
  #{templateName,jdbcType=VARCHAR}, #{version,jdbcType=VARCHAR}, #{intentionType,jdbcType=VARCHAR}, 
  #{dialogueTimes,jdbcType=INTEGER}, #{keyWords,jdbcType=VARCHAR}, #{crtTime,jdbcType=TIMESTAMP}, 
  #{crtUser,jdbcType=VARCHAR}, #{lstUpdateTime,jdbcType=TIMESTAMP}, #{lstUpdateUser,jdbcType=VARCHAR}
  )
