mapper.update:
update report_call_today
set call_count = call_count +1,duration_all=duration_all+ #{durationAll,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT SUM(duration_all) AS durationAll,DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate
FROM report_call_today

WHERE customer_id = #{customerId,jdbcType=VARCHAR}

SELECT  SUM(call_count) AS connect,DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate
FROM report_call_today WHERE intent != 'F'

  AND customer_id = #{customerId,jdbcType=VARCHAR}

SELECT SUM(call_count) AS duration5, DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate
FROM report_call_today WHERE duration_type = 1

  AND customer_id = #{customerId,jdbcType=VARCHAR}

SELECT SUM(call_count) AS duration10, DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate
FROM report_call_today WHERE duration_type = 2

  AND customer_id = #{customerId,jdbcType=VARCHAR}

SELECT SUM(call_count) AS duration30, DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate
FROM report_call_today WHERE duration_type = 3

  AND customer_id = #{customerId,jdbcType=VARCHAR}

SELECT SUM(duration_all) AS durationAll,call_date AS callDate
FROM report_call_day  WHERE call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR}

  AND customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY  call_date
ORDER BY callDate

SELECT  SUM(call_count) AS notConnect,  call_date AS callDate
FROM report_call_day  WHERE intent = 'F' AND  call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR}

  AND customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY  call_date
ORDER BY callDate

SELECT  SUM(call_count) AS connect, call_date AS callDate
FROM report_call_day WHERE intent != 'F' AND  call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR}

  AND customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY call_date
ORDER BY callDate

SELECT SUM(call_count) AS duration5, call_date AS callDate
FROM report_call_day WHERE duration_type = 1 AND  call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR}

  AND customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY  call_date
ORDER BY callDate

SELECT SUM(call_count) AS duration10, call_date AS callDate
FROM report_call_day WHERE duration_type = 2 AND  call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR}

  AND customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY  customer_id,call_date
ORDER BY callDate

SELECT SUM(call_count) AS duration30,  call_date AS callDate
FROM report_call_day WHERE duration_type = 3 AND  call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR}

  AND customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY  call_date
ORDER BY callDate

SELECT SUM(call_count) AS callCount, call_date AS callDate, intent FROM report_call_day where call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR} and intent IS NOT NULL
and customer_id = #{customerId,jdbcType=VARCHAR}
GROUP BY callDate,intent ORDER BY callDate

SELECT SUM(call_count) AS callCount,DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate, intent FROM report_call_today where intent IS NOT NULL
and customer_id = #{customerId,jdbcType=VARCHAR}
GROUP BY intent

SELECT CONCAT(IF(LENGTH(HOUR(call_time))=1,'0',''),HOUR(call_time),':00') AS callHour , SUM(out_count) AS outCount ,SUM(connect_count) AS connectCount , SUM(duration) AS duration
FROM report_call_hour
WHERE call_time >= #{startDate,jdbcType=TIMESTAMP} AND call_time <= #{endDate,jdbcType=TIMESTAMP}

  and customer_id = #{customerId,jdbcType=VARCHAR}

GROUP BY  callHour
ORDER BY callHour

SELECT SUM(call_count) AS callCount, call_date AS callDate, reason FROM report_call_day where call_date >=#{startDate,jdbcType=VARCHAR} AND call_date<=#{endDate,jdbcType=VARCHAR} and reason IS NOT NULL
and customer_id = #{customerId,jdbcType=VARCHAR}
GROUP BY callDate,reason ORDER BY callDate

SELECT SUM(call_count) AS callCount,DATE_FORMAT(NOW(),'%Y-%m-%d') as callDate, reason FROM report_call_today where reason IS NOT NULL
and customer_id = #{customerId,jdbcType=VARCHAR}
GROUP BY reason
