mapper.select:
select

  distinct

'true' as QUERYID,

from robot_call_his

  


  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into robot_call_his (id, seq_id, user_id, 
  org_code, ai_no, phone_no, 
  assign_time, template_id, call_status, 
  crt_date, crt_time, sellbot_callback_json
  )
values (#{id,jdbcType=INTEGER}, #{seqId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, 
  #{orgCode,jdbcType=VARCHAR}, #{aiNo,jdbcType=VARCHAR}, #{phoneNo,jdbcType=VARCHAR}, 
  #{assignTime,jdbcType=TIMESTAMP}, #{templateId,jdbcType=VARCHAR}, #{callStatus,jdbcType=INTEGER}, 
  #{crtDate,jdbcType=VARCHAR}, #{crtTime,jdbcType=TIMESTAMP}, #{sellbotCallbackJson,jdbcType=LONGVARCHAR}
  )
