mapper.select:
select

	distinct

'true' as QUERYID,

from dispatch_plan




	order by ${orderByClause}


	limit #{limitStart} , #{limitEnd}

select

from dispatch_plan

	where find_in_set(#{callHour},call_hour)


	and user_id = #{userId}


	and call_data = #{callData}


	and is_del = #{isDel}


	and status_plan = #{statusPlan}


	and status_sync = #{statusSync}


	and line = #{line}


	and flag = #{flag}

order by `gmt_create` ASC

	limit #{limitStart} , #{limitEnd}

select
user_id
from dispatch_plan

	where find_in_set(#{callHour},call_hour)


	and user_id = #{userId}


	and call_data = #{callData}


	and is_del = #{isDel}


	and status_plan = #{statusPlan}


	and status_sync = #{statusSync}


	and line = #{line}


	and flag = #{flag}

group by user_id

	limit #{limitStart} , #{limitEnd}

select
line
from dispatch_plan

	where find_in_set(#{callHour},call_hour)


	and user_id = #{userId}


	and call_data = #{callData}


	and is_del = #{isDel}


	and status_plan = #{statusPlan}


	and status_sync = #{statusSync}


	and flag = #{flag}

group by line

	limit #{limitStart} , #{limitEnd}

mapper.update:
