set status_plan = #{status}
where plan_uuid in

	#{item}

update dispatch_plan
set status_plan = #{status}
where plan_uuid in

	#{item}

update dispatch_plan
set status_sync = #{status}
where plan_uuid in

	#{item}

mapper.select:
select count(*) from dispatch_plan where 1=1

	and status_plan = #{statusPlan}


	and status_sync = #{statusSync}


	and flag = #{flag}


	and user_id = #{userId}
