mapper.select:
select

  distinct

'true' as QUERYID,

from sms_task_detail

  


  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into sms_task_detail (id, planuuid, task_name, 
  phone, send_type, send_status, 
  company_name, tunnel_name, send_time, 
  user_name, org_code, sms_content
  )
values (#{id,jdbcType=INTEGER}, #{planuuid,jdbcType=VARCHAR}, #{taskName,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{sendType,jdbcType=INTEGER}, #{sendStatus,jdbcType=INTEGER}, 
  #{companyName,jdbcType=VARCHAR}, #{tunnelName,jdbcType=VARCHAR}, #{sendTime,jdbcType=TIMESTAMP}, 
  #{userName,jdbcType=VARCHAR}, #{orgCode,jdbcType=VARCHAR}, #{smsContent,jdbcType=LONGVARCHAR}
  )
