use guiyu_linemarket;
DROP TABLE voip_gw_info;
CREATE TABLE voip_gw_info (id int NOT NULL AUTO_INCREMENT, gw_name varchar(20) NOT NULL COMMENT '网关名称(对应网关配置中的描述信息,用来匹配数据使用)', gw_brand varchar(20) COMMENT '网关品牌', gw_version varchar(30) COMMENT '网关型号', company_Id int COMMENT '网关公司号', dev_id int COMMENT '网关设备号', port_num int COMMENT '端口数', gw_status int COMMENT '1-正常,0-失效', sip_ip varchar(15) COMMENT '注册服务ip', sip_port int COMMENT '注册服务端口', line_port int COMMENT '线路端口(给呼叫中心使用)', start_sip_account int COMMENT '起始sip账号', start_sip_pwd int COMMENT '起始sip密码', sip_account_step int COMMENT 'sip账号步长', sip_pwd_step int COMMENT 'sip密码步长', univalent decimal(8,3) COMMENT '单价(元)', reg_type int COMMENT '语音网关注册类型:1-反向注册(gw-fs);2-正向注册(fs-gw)', gw_reg_status int COMMENT '语音网关注册状态: 0-初始化,1-确认', gw_ip varchar(15) COMMENT '网关外网ip', user_id varchar(50) COMMENT '归属人', org_code varchar(30) COMMENT '归属企业', crt_user varchar(50) COMMENT '创建人', crt_time datetime COMMENT '创建时间', update_time datetime, update_user varchar(50), PRIMARY KEY (id), INDEX voip_gw_info_idx1 (gw_name), INDEX voip_gw_info_idx2 (org_code), INDEX voip_gw_info_idx3 (company_Id), INDEX voip_gw_info_idx4 (dev_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='语音网关配置';
DROP TABLE voip_gw_port;
CREATE TABLE voip_gw_port (id int NOT NULL AUTO_INCREMENT, port int, line_id int COMMENT '线路编号', gw_status int COMMENT '1-正常,0-失效', alias varchar(30) COMMENT '别名', company_Id int COMMENT '网关公司号', dev_id int COMMENT '网关设备号', gw_id int, sip_account int, sip_pwd int, univalent decimal(8,3) COMMENT '单价(元)', phone_no varchar(20), user_id varchar(50) COMMENT '归属人', org_code varchar(30) COMMENT '归属企业', crt_user varchar(50) COMMENT '创建人', crt_time datetime COMMENT '创建时间', update_time datetime, update_user varchar(50), PRIMARY KEY (id), INDEX voip_gw_port_idx1 (gw_id), INDEX voip_gw_port_idx2 (port), INDEX voip_gw_port_idx3 (user_id), INDEX voip_gw_port_idx4 (org_code)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='语音网关端口配置';
DROP TABLE voip_gw_port_his;
CREATE TABLE voip_gw_port_his (id int NOT NULL AUTO_INCREMENT, company_Id int COMMENT '网关公司号', dev_id int COMMENT '网关设备号', port int, line_id int COMMENT '线路编号', gw_status int COMMENT '1-正常,0-失效', alias varchar(30) COMMENT '别名', gw_id int, sip_account int, sip_pwd int, univalent decimal(8,3) COMMENT '单价(元)', phone_no varchar(20), user_id varchar(50) COMMENT '归属人', org_code varchar(30) COMMENT '归属企业', crt_user varchar(50) COMMENT '创建人', crt_time datetime COMMENT '创建时间', PRIMARY KEY (id), INDEX voip_gw_port_his_idx1 (gw_id), INDEX voip_gw_port_his_idx2 (port)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='语音网关端口配置变更历史';